public class Program
{
    public static void Main(string[] args)
    {
        Log.Logger = new LoggerConfiguration()
            .WriteTo.Console()
            .WriteTo.File("logs/webapp.txt", rollingInterval: RollingInterval.Day)
            .CreateLogger();

        try
        {
            Log.Information("Запуск веб-приложения");
            CreateHostBuilder(args).Build().Run();
        }
        catch (Exception ex)
        {
            Log.Fatal(ex, "Приложение завершилось с ошибкой");
        }
        finally
        {
            Log.CloseAndFlush();
        }
    }

    public static IHostBuilder CreateHostBuilder(string[] args) =>
        Host.CreateDefaultBuilder(args)
            .UseSerilog() // Добавляем Serilog
            .ConfigureWebHostDefaults(webBuilder =>
            {
                webBuilder.UseStartup ();
            });
} В этом примере мы настроили Serilog и заменили встроенный логгер ASP.NET Core на Serilog с помощью метода UseSerilog()

