public class CustomMiddleware
{
    private readonly RequestDelegate _next;

    public CustomMiddleware(RequestDelegate next)
    {
        _next = next;
    }

    public async Task InvokeAsync(HttpContext context)
    {
        // Действие до следующего компонента
        await context.Response.WriteAsync("Hello from custom middleware before!\n");

        // Передача управления следующему компоненту
        await _next(context);

        // Действие после следующего компонента
        await context.Response.WriteAsync("Hello from custom middleware after!\n");
    }
} Этот пример демонстрирует базовый Middleware, который выполняет действия до и после передачи запроса следующему компоненту в конвейере

public void Configure(IApplicationBuilder app, IWebHostEnvironment env)
{
    if (env.IsDevelopment())
    {
        app.UseDeveloperExceptionPage();
    }

    app.UseRouting();
    app.UseAuthorization();

    app.UseMiddleware<CustomMiddleware>();

    app.UseEndpoints(endpoints =>
    {
        endpoints.MapControllers();
    });
} В этом примере сначала добавляются стандартные компоненты UseRouting и UseAuthorization , а затем пользовательский Middleware CustomMiddleware

